function slider(){let e=0;const t=document.querySelector(".slider__prev"),r=document.querySelector(".slider__next"),l=document.querySelector(".slider"),d=window.getComputedStyle(l).width.replace(/[a-z]+/g,""),n=document.querySelector(".slider__list"),s=document.querySelectorAll(".slider__item");s.forEach(e=>{e.style.width=d+"px"});const i=+d*s.length;r.addEventListener("click",()=>{e>=i-d?e=0:e+=+d,n.style.transform=`translateX(-${e}px)`}),t.addEventListener("click",()=>{e<=0?e=i-d:e-=+d,n.style.transform=`translateX(-${e}px)`})}slider(),window.addEventListener("resize",slider);