function slider(){let e=0;const t=document.querySelector(".slider__prev"),l=document.querySelector(".slider__next"),r=document.querySelector(".slider"),d=window.getComputedStyle(r).width.replace(/[a-z]+/g,""),s=document.querySelectorAll(".slider__item");s.forEach(e=>{e.style.width=d+"px"});const i=+d*s.length;l.addEventListener("click",()=>{e>=i-d?e=0:e+=+d,slidesField.style.transform=`translateX(-${e}px)`}),t.addEventListener("click",()=>{e<=0?e=i-d:e-=+d,slidesField.style.transform=`translateX(-${e}px)`})}slider(),window.addEventListener("resize",slider);